<script>
import CurriculumAdmissionMoreDialog from './CurriculumAdmissionMoreDialog';

export default {
  name: 'CurriculumAdmissions',
  components: {CurriculumAdmissionMoreDialog},
  props: {
    curriculum: Object,
  },
  data() {
    return {
      more: {
        show: false,
        body: '',
        header: '',
      },
    };
  },
  methods: {
    openMoreOlympiad() {
      this.more.body = this.curriculum.admission_olympiad_conditions;
      this.more.header = 'По олимпиаде';
      this.more.show = true;
    },
    openMoreAdditionalExams() {
      this.more.body = this.curriculum.admission_additional_exam_conditions;
      this.more.header = 'Дополнительные баллы к сумме ЕГЭ';
      this.more.show = true;
    },
  },
};
</script>

